export default {
  mounted() {
    this.initSlider();
  },

  methods: {
    initSlider() {
      const useIts = this.$refs.slider.querySelectorAll('.useIt');
      const yuans = this.$refs.slider.querySelectorAll('.yuan');
      const leftBtn = this.$refs.slider.querySelector('#button-l');
      const rightBtn = this.$refs.slider.querySelector('#button-r');
      const content = this.$refs.slider.querySelector('.slider');
      let index = 0;
      let timer = null; //清除class

      const clearclass = () => {
        for (let i = 0; i < useIts.length; i++) {
          useIts[i].className = "useIt";
          yuans[i].className = "yuan";
          yuans[i].setAttribute("num", i);
        }
      };
      /*只显示一个class*/


      function move() {
        clearclass();
        useIts[index].className = "useIt active";
        yuans[index].className = "yuan white";
      } //点击右边按钮切换下一张图片


      rightBtn.onclick = () => {
        if (index < useIts.length - 1) {
          index++;
        } else {
          index = 0;
        }

        move();
      }; //点击左边按钮切换上一张图片


      leftBtn.onclick = () => {
        if (index < useIts.length) {
          index--;
        } else {
          index = useIts.length - 1;
        }

        move();
      }; //开始定时器，点击右边按钮，实现轮播


      timer = setInterval(() => {
        rightBtn.onclick();
      }, 3000); //点击圆点时，跳转到对应图片

      yuans.forEach(yuan => {
        yuan.addEventListener("click", function () {
          const point_index = this.getAttribute("num");
          index = point_index;
          move();
        });
      }); //鼠标移入清除定时器，并开启一个三秒的定时器，使慢慢转动

      content.onmouseover = () => {
        clearInterval(timer);
        timer = setInterval(() => {
          rightBtn.onclick();
        }, 3000);
      }; //鼠标移出又开启定时器


      content.onmouseleave = () => {
        clearInterval(timer);
        timer = setInterval(() => {
          rightBtn.onclick();
        }, 1500);
      };
    }

  }
};
