export default {
  mounted() {
    this.initSlider();
  },

  methods: {
    setTab(m, n) {
      const tli = document.getElementById("menus" + m).getElementsByTagName("li");
      const tmo = document.getElementById("bmore" + m).getElementsByTagName("strong");
      const mli = document.getElementById("main" + m).getElementsByTagName("ul");

      for (let i = 0; i < tli.length; i++) {
        tli[i].className = i === n ? "hover" : "";
        mli[i].style.display = i === n ? "block" : "none";
        tmo[i].className = i === n ? "current02" : "";
      }
    }

  }
};
