export default {
  mounted() {
    this.initSlider();
  },

  methods: {
    initSlider() {
      const items = this.$refs.slider.querySelectorAll('.item');
      const circles = this.$refs.slider.querySelectorAll('.circle');
      const leftBtn = this.$refs.slider.querySelector('#btn-left');
      const rightBtn = this.$refs.slider.querySelector('#btn-right');
      const content = this.$refs.slider.querySelector('.slider');
      let index = 0;
      let timer = null; //清除class

      const clearclass = () => {
        for (let i = 0; i < items.length; i++) {
          items[i].className = "item";
          circles[i].className = "circle";
          circles[i].setAttribute("num", i);
        }
      };
      /*只显示一个class*/


      function move() {
        clearclass();
        items[index].className = "item active";
        circles[index].className = "circle white";
      } //点击右边按钮切换下一张图片


      rightBtn.onclick = () => {
        if (index < items.length - 1) {
          index++;
        } else {
          index = 0;
        }

        move();
      }; //点击左边按钮切换上一张图片


      leftBtn.onclick = () => {
        if (index < items.length) {
          index--;
        } else {
          index = items.length - 1;
        }

        move();
      }; //开始定时器，点击右边按钮，实现轮播


      timer = setInterval(() => {
        rightBtn.onclick();
      }, 3000); //点击圆点时，跳转到对应图片

      circles.forEach(circle => {
        circle.addEventListener("click", function () {
          const point_index = this.getAttribute("num");
          index = point_index;
          move();
        });
      }); //鼠标移入清除定时器，并开启一个三秒的定时器，使慢慢转动

      content.onmouseover = () => {
        clearInterval(timer);
        timer = setInterval(() => {
          rightBtn.onclick();
        }, 3000);
      }; //鼠标移出又开启定时器


      content.onmouseleave = () => {
        clearInterval(timer);
        timer = setInterval(() => {
          rightBtn.onclick();
        }, 1500);
      };
    },

    // 新增的函数，用于切换选项卡
    setTab(m, n) {
      const tli = document.getElementById("menus" + m).getElementsByTagName("li");
      const tmo = document.getElementById("bmore" + m).getElementsByTagName("strong");
      const mli = document.getElementById("main" + m).getElementsByTagName("ul");

      for (let i = 0; i < tli.length; i++) {
        tli[i].className = i === n ? "hover" : "";
        mli[i].style.display = i === n ? "block" : "none";
        tmo[i].className = i === n ? "current02" : "";
      }
    }

  }
};
